from unittest import TestCase
from app import app
from models import db, User

# Use test database and don't clutter tests with SQL
app.config['SQLALCHEMY_DATABASE_URI'] = 'postgresql:///blogly_test'
app.config['SQLALCHEMY_ECHO'] = False

# Make Flask errors be real errors, rather than HTML pages with error info
app.config['TESTING'] = True

# This is a bit of hack, but don't use Flask DebugToolbar
app.config['DEBUG_TB_HOSTS'] = ['dont-show-debug-toolbar']

db.drop_all()
db.create_all()

class UserViewsTest(TestCase):
    """Tests for views for Users."""

    def setUp(self):
        """Add sample pet."""

        User.query.delete()

        user = User(first_name="TestUser", last_name="TestLastName", image_url="data:image/jpeg;base64,")
        db.session.add(user)
        db.session.commit()

        self.user_id = user.id
        self.user = user

    def tearDown(self):
        """Clean up any fouled transaction."""

        db.session.rollback()

    def test_list_users(self):
        with app.test_client() as client:
            resp = client.get("/")
            html = resp.get_data(as_text=True)

            self.assertEqual(resp.status_code, 200)
            self.assertIn('TestUser', html)

    def test_show_user(self):
        with app.test_client() as client:
            resp = client.get(f"/{self.user_id}")
            html = resp.get_data(as_text=True)

            self.assertEqual(resp.status_code, 200)
            self.assertIn('<h6 class="display-4">TestUser TestLastName</h6>', html)
            self.assertIn(self.user.first_name, html)

    def test_add_user(self):
        with app.test_client() as client:
            d = {"first_name": "TestUser2", "last_name": "LastName", "picture": 'https://i.pinimg.com/originals/4f/f2/05/4ff2057a0e7c284d5aeb173d32c08f9f.jpg'}
            resp = client.post("/create_user", data=d, follow_redirects=True)
            html = resp.get_data(as_text=True)

            self.assertEqual(resp.status_code, 200)
            self.assertIn('<h6 class="display-4">TestUser2 LastName</h6>', html)
    
    def test_delete_user(self):
        with app.test_client() as client:
            user = User(first_name="TestUser3", last_name="TestLastName", image_url="data:image/jpeg;base64,")
            db.session.add(user)
            response = client.post(f'/{self.user_id}/delete', follow_redirects=True)
            # import pdb
            # pdb.set_trace()
            html = response.get_data(as_text=True)
            self.assertEqual(response.status_code, 200)



